const result = JSON.parse(msg.payload)
const date_time = result.date
const day = {
    0: "Sun",
    1: "Mon",
    2: "Tue",
    3: "Wed",
    4: "Thu",
    5: "Fri",
    6: "Sat"
}

const dateObj = new Date(date_time)
const minutes = dateObj.getMinutes() < 10 ? "0" + dateObj.getMinutes() : dateObj.getMinutes()
const hours = dateObj.getHours() < 10 ? "0" + dateObj.getHours() : dateObj.getHours()

const date_timeFormatted = day[dateObj.getDay()] + "-" + hours + ":" + minutes
const [date,time] = date_timeFormatted.split("-")


msg = {date_time, date_timeFormatted, date, time}
return msg
